var jshint=require("gulp-jshint"),ngmin=require("gulp-ngmin"),uglify=require("gulp-uglify"),gulp=require("gulp");gulp.task("lint",function(){return gulp.src("./*.js").pipe(jshint()).pipe(jshint.reporter("gulp-jshint-html-reporter",{filename:__dirname+"jshint-output.html",createMissingFolders:!1}))}),gulp.task("ngmin",function(){return gulp.src("angularapp.js").pipe(jshint()).pipe(ngmin({dynamic:!0})).pipe(gulp.dest("dist"))}),gulp.task("uglify",function(){return gulp.src("./*.js").pipe(uglify()).pipe(gulp.dest("dist"))});